import React from 'react'
import { Button, Form } from 'react-bootstrap'
import useAnswer from '../Utils/useAnswer'
import useFlags from '../Utils/useFlags'

import Exercises from './Exercises'

const Home = () => {
  const [expressions] = useFlags()
  const [, answOne, answTwo, answThree, answFour, answFive] = useAnswer()

  const submitHandler = (e) => {
    e.preventDefault()
    console.log(
      `one is( ${answOne}) two is( ${answTwo}) three is( ${answThree}) four is( ${answFour}) five is( ${answFive})`
    )
  }

  return (
    <div>
      <Form onSubmit={submitHandler}>
        {expressions.length !== 0 && (
          <>
            <Exercises
              _id={expressions[0].id}
              expression={expressions[0].question}
            />
            <Exercises
              _id={expressions[1].id}
              expression={expressions[1].question}
            />
            <Exercises
              _id={expressions[2].id}
              expression={expressions[2].question}
            />
            <Exercises
              _id={expressions[3].id}
              expression={expressions[3].question}
            />
            <Exercises
              _id={expressions[4].id}
              expression={expressions[4].question}
            />
          </>
        )}
        <Button variant='info' type='submit'>
          Submit
        </Button>
      </Form>
    </div>
  )
}

export default Home
