import React, { useContext } from 'react'
import { Button, Col, Container, Form, Row } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { GlobalContext } from '../hooks/GlobalContext'
import useFetch from '../hooks/useFetch'
import Exercises from './Exercises'

const Home = () => {
  const [expressions] = useFetch()
  const {
    answOne,
    answTwo,
    answThree,
    answFour,
    answFive,
    flag,
    setFlag,
  } = useContext(GlobalContext)

  const submitHandler = (e) => {
    e.preventDefault()
    console.log(`${answOne} ${answTwo} ${answThree} ${answFour} ${answFive}`)
    if (
      answOne === expressions[0].answer &&
      answTwo === expressions[1].answer &&
      answThree === expressions[2].answer &&
      answFour === expressions[3].answer &&
      answFive === expressions[4].answer
    ) {
      setFlag(true)
    } else {
      setFlag(false)
    }
    setTimeout(() => {
      console.log(flag)
    }, 500)
  }

  return (
    <Container>
      {expressions.length !== 0 && (
        <Container>
          <Exercises
            _id={expressions[0].id}
            expression={expressions[0].question}
          />
          <Exercises
            _id={expressions[1].id}
            expression={expressions[1].question}
          />
          <Exercises
            _id={expressions[2].id}
            expression={expressions[2].question}
          />
          <Exercises
            _id={expressions[3].id}
            expression={expressions[3].question}
          />
          <Exercises
            _id={expressions[4].id}
            expression={expressions[4].question}
          />
        </Container>
      )}
      <Row>
        {' '}
        <Col>
          <Button variant='info' type='submit' onClick={submitHandler}>
            Confirm
          </Button>
        </Col>
        <Col>
          <Button variant='info' type='submit' onClick={submitHandler}>
            Submit
          </Button>
          {!flag ? <Link to='/correct'></Link> : <Link to='/wrong'></Link>}
        </Col>
      </Row>
    </Container>
  )
}

export default Home
