const arrangeData = (data, difficulty) => {
  let index = []
  let newData = []
  const difficultQuestion = data.filter((d) => (d.difficulty = difficulty))

  while (index.length <= 4) {
    let r = Math.floor(Math.random() * difficultQuestion.length)
    if (index.indexOf(r) === -1) index.push(r)
  }
  index.forEach((elem) => newData.push(difficultQuestion[elem]))
  return newData
}

export default arrangeData
