import { createContext } from 'react'

export const GlobalContext = createContext(null)

const StoreProvider = ({ children }) => {
  const [answOne, setAnswOne] = useState()
  const [answTwo, setAnswTwo] = useState()
  const [answThree, setAnswThree] = useState()
  const [answFour, setAnswFour] = useState()
  const [answFive, setAnswFive] = useState()
  const [flag, setFlag] = useState(false)
  const [user, setUser] = useState({})
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState()
  const [submitDisable, setSubmitDisable] = useState(false)

  const store = {
    answOne: [answOne, setAnswOne],
    answTwo: [answTwo, setAnswTwo],
    answThree: [answThree, setAnswThree],
    answFour: [answFour, setAnswFour],
    answFive: [answFive, setAnswFive],
    flag: [flag, setFlag],
    user: [user, setUser],
    loading: [loading, setLoading],
    error: [error, setError],
    submitDisable: [submitDisable, setSubmitDisable],
  }
  return (
    <GlobalContext.Provider value={store}>{children}></GlobalContext.Provider>
  )
}

export default StoreProvider
