import React, { useContext } from 'react'
import { Container, Image } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { GlobalContext } from '../hooks/GlobalContext'
import { PlayCountContext } from '../hooks/PlayCountContext'

const Result = () => {
  const { flag } = useContext(GlobalContext)
  const { total, setTotal, success, setSuccess, fail, setFail } =
    useContext(PlayCountContext)
  // setTotal(total + 1)
  let imgSrc, title
  if (flag) {
    // setSuccess(success + 1)
    imgSrc = '/images/checkmark.jpg'
    title = 'Go Back'
  } else {
    // setFail(fail + 1)
    imgSrc = '/images/wrong.jpg'
    title = 'Try Again'
  }
  console.log(` Total ${total}, Success ${success}, Fail ${fail}`)
  const addState = () => {
    console.log('clicked')
  }

  return (
    <Container>
      <div className='py-3' onClick={addState}>
        <Link to='/' className='text-decoration-none'>
          {title}
        </Link>
      </div>
      <Image src={imgSrc} alt={imgSrc} fluid rounded />
    </Container>
  )
}

export default Result
