import React, { useContext } from 'react'
import { Container, Image } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { GlobalContext } from '../hooks/GlobalContext'

const Result = () => {
  const { flag } = useContext(GlobalContext)
  let imgSrc, title
  if (flag) {
    imgSrc = '/images/checkmark.jpg'
    title = 'Go Back'
  } else {
    imgSrc = '/images/wrong.jpg'
    title = 'Try Again'
  }

  return (
    <>
      {flag ? (
        <Container>
          <div className='py-3'>
            <Link to='/' className='text-decoration-none'>
              Go Back
            </Link>
          </div>
          <Image src='/images/checkmark.jpg' alt='correct' fluid rounded />
        </Container>
      ) : (
        <Container>
          <div className='py-3'>
            <Link to='/' className='text-decoration-none'>
              Try Again
            </Link>
          </div>
          <Image src='/images/wrong.jpg' alt='wrong' fluid />
        </Container>
      )}
    </>
  )
}

export default Result
