import { useState } from 'react'

const useAnswer = (_id) => {
  const [answOne, setAnswOne] = useState('')
  const [answTwo, setAnswTwo] = useState('')
  const [answThree, setAnswThree] = useState('')
  const [answFour, setAnswFour] = useState('')
  const [answFive, setAnswFive] = useState('')

  let valueTyped = (e) => {
    switch (_id) {
      case '0':
        setAnswOne(e.target.value)
        break
      case '1':
        setAnswTwo(e.target.value)
        break
      case '2':
        setAnswThree(e.target.value)
        break
      case '3':
        setAnswFour(e.target.value)
        break
      case '4':
        setAnswFive(e.target.value)
        break
      default:
        break
    }
  }

  console.log(
    `one is ${answOne} two is ${answTwo} three is ${answThree} four is ${answFour} five is ${answFive}`
  )

  return [valueTyped, answOne, answTwo, answThree, answFour, answFive]
}

export default useAnswer
