import React, { useState } from 'react'
import { Col, Form, Row } from 'react-bootstrap'
import useFlags from '../Utils/useFlags'

const Exercises = ({ _id, expression }) => {
  const [answOne, setAnswOne] = useState([])
  const [answTwo, setAnswTwo] = useState([])
  const [answThree, setAnswThree] = useState([])
  const [answFour, setAnswFour] = useState([])
  const [answFive, setAnswFive] = useState([])

  let valueTyped = (e) => {
    switch (_id) {
      case '0':
        setAnswOne(e.target.value)
        break
      case '1':
        setAnswOne(e.target.value)
        break
      case '2':
        setAnswOne(e.target.value)
        break
      case '3':
        setAnswOne(e.target.value)
        break
      case '4':
        setAnswOne(e.target.value)
        break
      default:
        break
    }
  }

  return (
    <Form.Group
      as={Row}
      className='my-3 justify-content-between mx-auto px-2'
      key={_id}
    >
      <Form.Label column md={4} className=' shadow bg-body rounded my-2'>
        {expression}
      </Form.Label>
      <Col>
        <Form.Control
          onChange={valueTyped}
          plaintext
          className=' shadow bg-body rounded my-2 px-2'
        />
      </Col>
    </Form.Group>
  )
}

export default Exercises
