import { useState, useEffect } from 'react'
import axios from 'axios'

const useFlags = () => {
  const [expressions, setExpressions] = useState([])
  const [answOne, setAnswOne] = useState()
  const [answTwo, setAnswTwo] = useState()
  const [answThree, setAnswThree] = useState()
  const [answFour, setAnswFour] = useState()
  const [answFive, setAnswFive] = useState()

  let valueTyped = (e) => {
    switch (_id) {
      case '0':
        setAnswOne(e.target.value)
        break
      case '1':
        setAnswTwo(e.target.value)
        break
      case '2':
        setAnswThree(e.target.value)
        break
      case '3':
        setAnswFour(e.target.value)
        break
      case '4':
        setAnswFive(e.target.value)
        break
      default:
        break
    }
  }

  useEffect(() => {
    const fetchData = async () => {
      const { data } = await axios.get('/api/guess')
      setExpressions(data)
    }
    fetchData()
  }, [])

  return [expressions, answOne, answTwo, answThree, answFour, answFive]
}

export default useFlags
