import React, { useContext } from 'react'
import { Col, Container, Image, Row } from 'react-bootstrap'
import { GlobalContext } from '../hooks/GlobalContext'


const Home = () => {
  const { user } = useContext(GlobalContext)

  return (
    <Container>
      {user ? (
        <Row className='p-1 text-info'>
          Welcome {user.name}
          <Image src='/images/002a.png' alt='game' fluid rounded />
        </Row>) : (
        <div >
          <h2 className=''>Please Login To Start The Race </h2>
            <Row className="text-center">
              <Col><p>Welcome G</p></Col>
              <Col><Image src='/images/thinking-child.png' alt='game' fluid rounded className='images' /></Col>
          </Row>
        </div>
      )}
    </Container>
  )
}

export default Home
